import 'package:sqflite/sqflite.dart';import 'package:path/path.dart';class SqlDb{  static Database? _db;  Future <Database?> get db async{    if(_db == null){      _db=await intialDb();      return _db;    }else{      return _db;    }  }  intialDb() async{    print("===============================================intialDb");    String dataBasePath=await getDatabasesPath();    String path =join(dataBasePath,'notification_mobile.db');    Database mydb = await openDatabase(path,onCreate: _onCreate,version: 3,onUpgrade: _onUpgrade);return mydb;  }  _onUpgrade(Database db ,int oldversion, int newversion ){    print("===============================================_onUpgrade");  }  _onCreate(Database db , int version)async{   await db.execute('''      CREATE TABLE "News"(   "id" INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,   "Title" TEXT NOT NULL,   "boby" TEXT NOT NULL   )   ''');   print("================CREATE TABLE================");  }   readData(String sql)async{    Database? mydb = await db;    List<Map> response =await mydb!.rawQuery(sql);    return response;   }  insertData(String sql)async{    Database? mydb = await db;    int response =await mydb!.rawInsert(sql);    return response;  }  updatetData(String sql)async{    Database? mydb = await db;    int response =await mydb!.rawInsert(sql);    return response;  }  deleteData(String sql)async{    Database? mydb = await db;    int response =await mydb!.rawInsert(sql);    return response;  }}