import 'package:firebase_messaging/firebase_messaging.dart';import 'package:flutter/widgets.dart';import 'package:get/get_rx/src/rx_types/rx_types.dart';class FirebaseMessagingService {  static RxBool onMessage=false.obs;  static  FirebaseMessaging _fcm = FirebaseMessaging.instance;  static Future<void> configureFirebaseMessaging() async {    NotificationSettings settings = await _fcm.requestPermission(      alert: true,      badge: true,      sound: true,      announcement: true,      criticalAlert: true,      provisional: true    );    if (settings.authorizationStatus == AuthorizationStatus.authorized) {      _fcm.getToken().then((String? token) {        print('FCM Token: $token');      });    }    FirebaseMessaging.onMessageOpenedApp.listen((RemoteMessage message) {      if (message.notification !=null) {        print('Received news message in background: ${message.notification?.title}');      }    });    RemoteMessage? initialMessage = await FirebaseMessaging.instance.getInitialMessage();    if (initialMessage != null) {      if (initialMessage.notification != null) {        print('Received news message when terminated: ${initialMessage.notification?.title}');      }    }  }}