import 'package:firebase_messaging/firebase_messaging.dart';import 'package:flutter/widgets.dart';import 'package:get/get_rx/src/rx_types/rx_types.dart';class FirebaseMessagingService {  static RxBool onMessage=false.obs;  static  FirebaseMessaging _fcm = FirebaseMessaging.instance;  static Future<void> configureFirebaseMessaging() async {    NotificationSettings settings = await _fcm.requestPermission(      alert: true,      badge: true,      sound: true,      announcement: true,      criticalAlert: true,      provisional: true,    );    if (settings.authorizationStatus == AuthorizationStatus.authorized) {      print('User granted permission');    } else if (settings.authorizationStatus == AuthorizationStatus.provisional) {      print('User granted provisional permission');    } else {      print('User declined or has not accepted permission');    }    FirebaseMessaging.onMessageOpenedApp.listen((RemoteMessage message) {      if (message.notification !=null) {        print('Received newsqqqqqqqqqqqqqqq message in background: ${message.notification?.title}');        print("2222222222222222222222222222222222222222222222222222222222222222222");      }    });    RemoteMessage? initialMessage = await FirebaseMessaging.instance.getInitialMessage();    if (initialMessage != null) {      if (initialMessage.notification != null) {        print('Received news message when terminated: ${initialMessage.notification?.title}');        print("111111111111111111111111111111111111111111111111111111111111111111111");      }    }  }}