import 'package:flutter/cupertino.dart';import '../../help/Colors.dart';class MyDivider1 extends StatelessWidget {  const MyDivider1({    Key? key, required this.width, required this.height,  }) : super(key: key);  final double width;  final double height;  @override  Widget build(BuildContext context) {    return Container(      width: width,      height: height,      decoration: BoxDecoration(          color: Colorsapp.mainColor,          borderRadius: const BorderRadius.all(            Radius.circular(50),          ),          border: Border.all(color: Colorsapp.mainColor, width: 2)),    );  }}