import 'package:flutter/material.dart';import 'package:google_nav_bar/google_nav_bar.dart';import 'package:line_icons/line_icons.dart';import 'package:notificationsystem_mobile/help/text_style.dart';import '../help/Colors.dart';import 'News_page/News_main_page.dart';class HomePage extends StatefulWidget {  @override  _HomePageState createState() => _HomePageState();}class _HomePageState extends State<HomePage> {  int _selectedIndex = 0;  static const TextStyle optionStyle =  TextStyle(fontSize: 30, fontWeight: FontWeight.w600);  static const List<Widget> _widgetOptions = <Widget>[    News_main_page(),    Text(      'Likes',      style: optionStyle,    ),    Text(      'Search',      style: optionStyle,    ),    Text(      'Profile',      style: optionStyle,    ),  ];  @override  Widget build(BuildContext context) {    return SafeArea(        child: Scaffold(      backgroundColor: Colors.white,      body: _widgetOptions.elementAt(_selectedIndex),      bottomNavigationBar: Container(        decoration: BoxDecoration(          color: Colors.white,          boxShadow: [            BoxShadow(              blurRadius: 20,              color: Colors.black.withOpacity(.5),            )          ],        ),        child: Padding(          padding: const EdgeInsets.symmetric(horizontal: 15.0, vertical: 5),          child: GNav(            rippleColor: Colors.grey[300]!,            hoverColor: Colors.grey[100]!,            gap: 5,            activeColor: Colors.white,            iconSize: 20,            padding: EdgeInsets.symmetric(horizontal: 20, vertical: 8),            duration: Duration(milliseconds: 400),            tabBackgroundColor: Colorsapp.mainColor,            color: Colorsapp.mainColor,            tabs:  [              GButton(                icon: Icons.home_filled,                text: '',                textStyle: Text_Style.getstyle(              fontWeight: FontWeight.w500,              ColorText: Colorsapp.mainColor,              fontsize: 13          ),              ),              GButton(                icon: LineIcons.calendar,                text: '',                textStyle: Text_Style.getstyle(                  fontWeight: FontWeight.w500,                  ColorText: Colorsapp.mainColor,                  fontsize: 13              ),              ),              GButton(                icon: LineIcons.hackerNewsSquare,                text: '',                textStyle: Text_Style.getstyle(                    fontWeight: FontWeight.w500,                    ColorText: Colorsapp.mainColor,                    fontsize: 13                ),              ),               GButton(                icon: Icons.account_circle,                text: '',                 textStyle: Text_Style.getstyle(                     fontWeight: FontWeight.w500,                     ColorText: Colorsapp.mainColor,                     fontsize: 13                 ),              ),            ],            selectedIndex: _selectedIndex,            onTabChange: (index) {              setState(() {                _selectedIndex = index;              });            },          ),        ),      ),    ));  }}