import 'package:flutter/material.dart';import 'package:flutter_snake_navigationbar/flutter_snake_navigationbar.dart';import 'package:google_nav_bar/google_nav_bar.dart';import 'package:line_icons/line_icons.dart';import 'package:notificationsystem_mobile/View/profile/Profile_main_page.dart';import 'package:notificationsystem_mobile/controler/method.dart';import 'package:notificationsystem_mobile/help/text_style.dart';import '../help/Colors.dart';import 'News_page/News_main_page.dart';import 'Schedules_page/Schedules_main_page.dart';class HomePage extends StatefulWidget {  @override  _HomePageState createState() => _HomePageState();}class _HomePageState extends State<HomePage> {  ShapeBorder? bottomBarShape = const RoundedRectangleBorder(    borderRadius: BorderRadius.only(      topRight: Radius.circular(25),      topLeft:Radius.circular(25), ),  );  SnakeBarBehaviour snakeBarStyle = SnakeBarBehaviour.pinned;  int _selectedItemPosition = 0;  SnakeShape snakeShape = SnakeShape.circle;  bool showSelectedLabels = true;  bool showUnselectedLabels = false;  Color selectedColor = Colorsapp.mainColor;  Color unselectedColor = Colors.blueGrey;  Color? containerColor;  ///////////////////////////////////////////////  static const TextStyle optionStyle =  TextStyle(fontSize: 30, fontWeight: FontWeight.w600);  static  List<Widget> _widgetOptions = <Widget>[    News_main_page(),    Schedules_main_page(),    Profile_main_page()  ];  @override  Widget build(BuildContext context) {    return SafeArea(        child: Scaffold(      backgroundColor: Colors.white,      body: _widgetOptions.elementAt(_selectedItemPosition),      bottomNavigationBar: SnakeNavigationBar.color(        height: 55,        elevation: 50,        behaviour: snakeBarStyle,        snakeShape: snakeShape,        shape: bottomBarShape,        padding: EdgeInsets.zero,        ///configuration for SnakeNavigationBar.color        snakeViewColor: selectedColor,        selectedItemColor:        snakeShape == SnakeShape.indicator ? selectedColor : null,        unselectedItemColor: unselectedColor,        ///configuration for SnakeNavigationBar.gradient        // snakeViewGradient: selectedGradient,        // selectedItemGradient: snakeShape == SnakeShape.indicator ? selectedGradient : null,        // unselectedItemGradient: unselectedGradient,        showUnselectedLabels: showUnselectedLabels,        showSelectedLabels: showSelectedLabels,        currentIndex: _selectedItemPosition,        onTap: (index) => setState(() => _selectedItemPosition = index),        items: const [          BottomNavigationBarItem(              icon: Icon(Icons.newspaper_sharp),              label: 'tickets'),          BottomNavigationBarItem(              icon: Icon(Icons.calendar_today), label: ''),          BottomNavigationBarItem(              icon: Icon(Icons.person), label: ''),        ],        selectedLabelStyle: const TextStyle(fontSize: 14),        unselectedLabelStyle: const TextStyle(fontSize: 10),      ),    ));  }}