import 'package:flutter/material.dart';import '../../help/Colors.dart';import '../../help/text_style.dart';class News_Notification_Card extends StatelessWidget {  const News_Notification_Card({    Key? key,    required this.data, required this.time, required this.author, required this.title, required this.stage, required this.color,  }) : super(key: key);  final Map<String, dynamic> data;  final String time;  final String author;  final String title;  final String stage;  final Color color;  @override  Widget build(BuildContext context) {    return Card(      color: color,      shape: RoundedRectangleBorder(        borderRadius: BorderRadius.circular(10.0),        side: BorderSide(color: Colorsapp.mainColor, width: 3.0),      ),      elevation: 5,      child: Container(        padding: const EdgeInsets.all(2),        decoration: const BoxDecoration(        ),        child: Padding(          padding: const EdgeInsets.all(8.0),          child: Column(            mainAxisAlignment: MainAxisAlignment.spaceAround,            crossAxisAlignment: CrossAxisAlignment.end,            children: [              Text(                textDirection: TextDirection.rtl,                "تم ارسال خبر جديد بواسطة $author",                style: Text_Style.getstyle(                fontWeight: FontWeight.bold,                ColorText: Colors.black.withOpacity(0.7),                fontsize:                MediaQuery                    .of(context)                    .size                    .width / 20,              ),),              const SizedBox(                height: 5,              ),              Text(                textDirection: TextDirection.rtl,                textAlign: TextAlign.right,                title,                style: Text_Style.getstyle(                  fontWeight: FontWeight.w400,                  ColorText: Colors.black.withOpacity(0.7),                  fontsize:                  MediaQuery                      .of(context)                      .size                      .width / 20,                ),),              const SizedBox(                height: 5,              ),              Text(                textDirection: TextDirection.rtl,                "منذ $time",                style: Text_Style.getstyle(                  fontWeight: FontWeight.w500,                  ColorText: Colorsapp.redColor.withOpacity(0.7),                  fontsize:                  MediaQuery                      .of(context)                      .size                      .width / 25,                ),),            ],          ),        ),      ),    );  }}