import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import '../../help/Colors.dart';import '../../help/text_style.dart';class Button_Profile extends StatelessWidget {  const Button_Profile({    Key? key, required this.title, required this.onPressed,  }) : super(key: key);  final String title;  final void Function() onPressed;  @override  Widget build(BuildContext context) {    return Container(      margin: EdgeInsets.all(5),      width: MediaQuery.of(context).size.width/1.2,      height: 50,      decoration: BoxDecoration(          color: Colorsapp.mainColor,          borderRadius: BorderRadius.circular(20)      ),      child: ElevatedButton(        style: ElevatedButton.styleFrom(            shape:RoundedRectangleBorder(              borderRadius: BorderRadius.circular(20), // <-- Radius            ),            elevation:5,            backgroundColor: Colorsapp.mainColor        ),        onPressed:() async {          onPressed();          // Get the App Check token.        },        child: Text(          "${title}",          style: Text_Style.getstyle(              fontsize: MediaQuery.of(context).size.width / 23,              ColorText: Colorsapp.White,              fontWeight: FontWeight.w700),        ),      ),    );  }}