import 'package:shared_preferences/shared_preferences.dart';class UserPreferences {  static final String _emailKey = 'email';  static final String _passwordKey = 'password';  static Future<void> saveCredentials(String email, String password) async {    SharedPreferences prefs = await SharedPreferences.getInstance();    await prefs.setString(_emailKey, email);    await prefs.setString(_passwordKey, password);  }  static Future<String?> getEmail() async {    SharedPreferences prefs = await SharedPreferences.getInstance();    return prefs.getString(_emailKey);  }  static Future<String?> getPassword() async {    SharedPreferences prefs = await SharedPreferences.getInstance();    return prefs.getString(_passwordKey);  }  static Future<void> clearCredentials() async {    SharedPreferences prefs = await SharedPreferences.getInstance();    await prefs.remove(_emailKey);    await prefs.remove(_passwordKey);  }}