ring(_passwordKey);  }  static Future<bool?> getstate() async {    SharedPreferences prefs = await SharedPreferences.getInstance();    print("_isLogIn: ${prefs.getBool(_isLogIn)}");    return prefs.getBool(_isLogIn);  }  static Future<void> clearCredentials() async {    SharedPreferences prefs = await SharedPreferences.getInstance();    await prefs.remove(_emailKey);    await prefs.remove(_passwordKey);    await prefs.remove(_isLogIn);    await prefs.remove(_stage);    await prefs.remove(_name);    await prefs.remove(documentID);  }}