import 'package:flutter/material.dart';import 'package:flutter/src/painting/text_style.dart';import 'package:google_fonts/google_fonts.dart';import 'Colors.dart';class Text_Style {  static TextStyle? getstyle(      {required double? fontsize,      required Color? ColorText,      required FontWeight? fontWeight}      )  {    return GoogleFonts.tajawal(      textStyle: TextStyle(        fontFamily: 'Al Jazeera Arabic',        fontSize: fontsize,        color: ColorText,        fontWeight:fontWeight      ),    );  }}