import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';class MyForm extends StatefulWidget {  @override  _MyFormState createState() => _MyFormState();}class _MyFormState extends State<MyForm> {  final _formKey = GlobalKey<FormState>();  String? _email;  @override  Widget build(BuildContext context) {    return Form(      key: _formKey,      child: Column(        children: [          TextFormField(            decoration: InputDecoration(              labelText: 'Email',            ),            validator: (value) {              if (value!.isEmpty) {                return 'Please enter an email address';              }              if (!value.contains('@')) {                return 'Please enter a valid email address';              }              return null;            },            onSaved: (value) {              _email = value;            },          ),          ElevatedButton(            onPressed: () {              if (_formKey.currentState!.validate()) {                _formKey.currentState!.save();                print('Email: $_email');              }            },            child: Text('Submit'),          ),        ],      ),    );  }}